import React from 'react';
import logo from './logo.svg';
import './App.css';

function Increment(props){
  return 8;
}

function Countdown(props){


  return <>
  <label>Contador</label>
  <input type="text" value={props.countdown}></input>

  </>;
}

class InputExample extends React.Component {
  state = {
    currentCount: this.props.defaultCount,
    running: false,
    value: 0
 }
  render() {
  return (<>
  <input
  type="text"
  value={this.state.value}
  onChange={event => this.setState({ currentCount: parseInt(event.target.value) })}>
  </input>
  <button onClick={event => this.setState({ currentCount: parseInt(event.target.value) })}>START</button>
  </>
  );
  }
  }

function App() {
  return (
    <InputExample defaultCount="4"/>
  );
}

export default App;
