import React from 'react';
import axios from 'axios';
import './App.css';

class ExchangeRate extends React.Component {



  render() {
    return (<label>El {this.props.baseCurrency} vale {this.props.relativeValue} {this.props.displayCurrency}</label>)
  }
}


class App extends React.Component {
  state = {
    relativeValue: "35"
  }

  componentDidMount(props) {
    console.log(this.props.displayCurrency);
    axios.get("https://api.exchangeratesapi.io/latest?base=" + this.props.baseCurrency).then(result => {
        this.setState(prevState => {
          //  this.state.relativeValue= result.data.rates[this.props.displayCurrency]
          this.state.relativeValue= result.data.rates.USD
        }
        );

    }); 
  }


  render (){
    return (
      <ExchangeRate baseCurrency="JPY" relativeValue={this.state.relativeValue} displayCurrency="USD"  />
    );
  }
}

export default App;
