import React from 'react';
import axios from 'axios';
import './App.css';

class ExchangeRate extends React.Component {

  render() {
    return (<label>El {this.props.baseCurrency} vale {this.props.relativeValue} {this.props.displayCurrency}</label>)
  }
}

class SelectorRate extends React.Component {

  handleChange = e => this.setState({ value: e.target.value });

  changeSearch = (e) => {
    e.preventDefault();
    var type = e.target.value;
    this.props.changeSearchState(this.props.name,type)
  }

  render() {
    return (<select name={this.props.name}
                    onChange={this.changeSearch} >
    {
      this.props.currencies.map ( currency => <option value={currency}>{currency}</option>)
    }
    </select>)
  }
}


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      baseCurrency: "JPY",
      displayCurrency: "USD",
      relativeValue: "35"
    }
    this.changeSearchState = this.changeSearchState.bind(this);
  }

  changeSearchState = (name,dataType) => {
    console.log(name)
    this.setState({
      [name]: dataType
    });

  }

  changeRelative = (prevState) => {
    console.log(this.state.baseCurrency);
    axios.get("https://api.exchangeratesapi.io/latest?base=" + this.state.baseCurrency).then(result => {
    this.setState({
        relativeValue : result.data.rates.USD
      });
    })
  }

  componentDidMount() {
    this.changeRelative();
  }

  componentDidUpdate() {
    this.changeRelative();
  }

  render() {
    return (
      <>
      <ExchangeRate baseCurrency={this.state.baseCurrency} relativeValue={this.state.relativeValue} displayCurrency={this.state.displayCurrency} />
      <SelectorRate name="baseCurrency" changeSearchState={this.changeSearchState} currencies={["EUR","USD","JPY"]} />
      <SelectorRate name="displayCurrency" changeSearchState={this.changeSearchState} currencies={["EUR","USD","JPY"]} />                    
      </>
    );
  }
}

export default App;
