import React from 'react';
import axios from 'axios';
import './App.css';

class ExchangeRate extends React.Component {
  state = {
    currency: ""
  }

  componentDidMount() {
    axios.get("https://api.exchangeratesapi.io/latest?base=EUR").then(result => {
      this.setState({
        currency: result.data.rates.USD
      });
    });
  }
  render() {
    return (<label>El Euro vale {this.state.currency} dolares</label>)
  }
}


function App() {
  return (
    <ExchangeRate />
  );
}

export default App;
