import React from 'react';
import axios from 'axios';
import './App.css';

class ExchangeRate extends React.Component {
  render() {
    console.log("render->" + this.props.relativeValue)
    return (<label>El {this.props.baseCurrency} vale {this.props.relativeValue} {this.props.displayCurrency}</label>)
  }
}

class SelectorRate extends React.Component {
  render() {
    console.log("render->" + this.props.name)
    return (<select name={this.props.name}
                    onChange={this.props.onCurrencyChange} >
    {
      this.props.currencies.map ( currency => <option value={currency}>{currency}</option>)
    }
    </select>)
  }
}


class App extends React.Component {
  state = {
    relativeValue: "35"
  }

  componentDidMount() {
    console.log(this.state.relativeValue);
    axios.get("https://api.exchangeratesapi.io/latest?base=EUR").then(result => {
    this.setState({
        relativeValue : result.data.rates.USD
      }
    );
    console.log(this.state.relativeValue);

    });
  }

  render() {
    return (
      <>
      <ExchangeRate baseCurrency="JPY" relativeValue={this.state.relativeValue} displayCurrency="USD" />
      <SelectorRate name={this.state.baseCurrency} onCurrencyChange={console.log('funciona')} currencies={["EUR","USD","JPY"]} />
      <SelectorRate onCurrencyChange={console.log('funciona')} currencies={["EUR","USD","JPY"]} />                    
      </>
    );
  }
}

export default App;
