import React from 'react';
import axios from 'axios';
import './App.css';

class ExchangeRate extends React.Component {
  state = {
    relativeValue: "",
    baseCurrency: "EUR",
    displayCurrency: "USD"
  }

  componentDidMount() {
    axios.get("https://api.exchangeratesapi.io/latest?base=" + this.state.baseCurrency).then(result => {
      this.setState({
        currency: result.data.rates.USD
      });
    });
  }
  render() {
    return (<label>El {this.state.baseCurrency} vale {this.state.relativeValue} dolares</label>)
  }
}


function App() {
  return (
    <ExchangeRate />
  );
}

export default App;
