    baseCurrency: this.props.baseCurrency,
    displayCurrency: "USD"
  }

  componentDidMount(props) {
    /*axios.get("https://api.exchangeratesapi.io/latest?base=" + this.state.baseCurrency).then(result => {
      this.setState(prevState => {
        relativeValue: result.data.rates[prevState.displayCurrency]
      });
    }); */
  }
  render() {
    return (<label>El {this.props.baseCurrency} vale {this.state.relativeValue} {this.state.displayCurrency}</label>)
  }
}


function App() {
  return (
    <ExchangeRate baseCurrency="JPY"/>
  );
}

export default App;
