import React from 'react';
import axios from 'axios';
import './App.css';

class ExchangeRate extends React.Component {


  componentDidMount(props) {
    axios.get("https://api.exchangeratesapi.io/latest?base=" + this.state.baseCurrency).then(result => {
      this.setState(prevState => {
        relativeValue: result.data.rates[this.props.displayCurrency]
      });
    }); 
  }
  render() {
    return (<label>El {this.props.baseCurrency} vale {this.props.relativeValue} {this.props.displayCurrency}</label>)
  }
}


function App() {
  return (
    <ExchangeRate baseCurrency="JPY" relativeValue="35" displayCurrency="USD"  />
  );
}

export default App;
