import React from 'react';
import axios from 'axios';
import './App.css';

class ExchangeRate extends React.Component {

  render() {
    return (<label>El {this.props.baseCurrency} vale {this.props.relativeValue} {this.props.displayCurrency}</label>)
  }
}

class SelectorRate extends React.Component {

  handleChange = e => this.setState({ value: e.target.value });

  changeSearch = (e) => {
    e.preventDefault();
    var type = e.target.value;
    this.props.changeSearchState(type,"baseCurrency")
  }

  render() {
    return (<select name={this.props.name}
                    onChange={this.changeSearch} >
    {
      this.props.currencies.map ( currency => <option value={currency}>{currency}</option>)
    }
    </select>)
  }
}


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      baseCurrency: "JPY",
      displayCurrency: "USD",
      relativeValue: "35"
    }
    this.changeSearchState = this.changeSearchState.bind(this);
  }

  changeSearchState = (dataType,name) => {
    this.setState({
      baseCurrency: dataType
    });
  }

  componentDidMount() {
    axios.get("https://api.exchangeratesapi.io/latest?base=EUR").then(result => {
    this.setState({
        relativeValue : result.data.rates.USD
      }
    );


    });
  }

  render() {
    return (
      <>
      <ExchangeRate baseCurrency={this.state.baseCurrency} relativeValue={this.state.relativeValue} displayCurrency="USD" />
      <SelectorRate name="baseCurrency" changeSearchState={this.changeSearchState} currencies={["EUR","USD","JPY"]} />
      <SelectorRate changeSearchState={this.changeSearchState} currencies={["EUR","USD","JPY"]} />                    
      </>
    );
  }
}

export default App;
