import React from 'react';
import './App.css';
import Counter from './Counter.js';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import todoApp from './reducer'





/*class Increment extends React.Component {
  function handleIncrement(e) {
    console.log("handleIncrement");
    store.dispatch(addIncrement(this.props.value));
  }

  render() {
    return (
      <>
        <button onClick={event => handleIncrement()}>Increment</button>
      </>)
  }
}*/

function handleIncrement(e) {
  console.log("handleIncrement");
  store.dispatch(addIncrement(e.target.value));
}

const Increment = (props) => <button onClick={event => handleIncrement(event)}>Increment</button>;


const addIncrement = (number) => {
  return {
    type: "INCREMENT",
    payload: number
  };

}



let store = createStore(todoApp)


function App() {
  return (
    <>
      <Provider store={store}>
        <Counter counter="0"/>
        <Increment  value="1"/>
        <Increment  value="-1"/>
      </Provider>
    </>
  );
}

export default App;
