import React from 'react';
import './App.css';
import { createStore } from 'redux';
import { Provider } from 'react-redux';
import todoApp from './reducer'


class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0
    }
  }

  render() {
    return (
      <>
        <h1>{this.state.counter}</h1>
      </>)
  }
}

function handleIncrement(e) {
  console.log("handleIncrement");
  store.dispatch(addIncrement("1"));
}

const Increment = () => <button onClick={event => handleIncrement()}>Increment</button>;


const addIncrement = (text) => {
  {
      return {
        type: "INCREMENT",
        payload: text
      };
  }
}

let store = createStore(todoApp)

  function App() {
    return (
      <>
      <Provider store={store}>
        <Counter />
        <Increment />
        </Provider> 
      </>
    );
  }

  export default App;
