function todosCounter(state = 0, action) {
    switch (action.type) {
        case 'INCREMENT':
            return state + action.payload
        case 'DECREMENT':
            return state - action.payload
        default:
            return state
    }
}


function todoApp(state = {}, action) {
    return {
        counter: todosCounter(state.counter, action)
    };
}

export default todoApp;
