import React from 'react';
import './App.css';


class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0
    }
  }

  render() {
    return (
      <>
      <h1>{this.state.counter}</h1>
      </>)
  }
}

 const Increment = () => <button>Increment</button>;




function App() {
  return (
    <>
    <Counter />
    <Increment />
    </>
  );
}

export default App;
