import React from 'react';
import './App.css';




class Platforms extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      platforms: []
    }
  }
  componentDidUpdate(){
    this.fetchPositions();
  }

  componentDidMount(){
    this.fetchPositions();
  }

  fetchPositions = () =>  {
    axios.get("https://libraries.io/api/platforms")
      .then(result => {
        this.setState({
          platforms: result
        });
      })
      .catch(function (error) {
        console.log(error);
      });

  }

  render() {
    return (
      <>
      {this.state.platforms.map ( platform => <h2>{platform.name}</h2>)}
      </>)
  }
}




function App() {
  return (
    <div className="App">
      <Platforms  />
    </div>
  );
}

export default App;
