  constructor(props) {
    super(props);
    this.state = {
      platforms: []
    }
  }
  componentDidUpdate(){
    this.fetchPositions();
  }

  componentDidMount(){
    this.fetchPositions();
  }

  fetchPositions = () =>  {
    axios.get("https://libraries.io/api/platforms")
      .then(result => {
        this.setState({
          platforms: result.data
        });
      })
      .catch(function (error) {
        console.log(error);
      });

  }

